import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import CloudMetric from './CloudMetric';
import Alert from './Alert';
import { loginGoogle, loginAWS } from './services/api';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');

  const handleGoogleLogin = () => {
    loginGoogle();
  };

  const handleAWSLogin = async () => {
    try {
      const awsToken = await loginAWS('your-username', 'your-password');
      console.log('AWS Token:', awsToken);
    } catch (error) {
      console.error('Error logging in to AWS:', error);
    }
  };

  return (
    <div className="dashboard">
      <Header />
      <div className="dashboard-content">
        <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} />
        <main className="main">
          {activeSection === 'dashboard' && (
            <>
              <h2>Real-Time Cloud Monitoring</h2>
              <div className="metrics">
                <CloudMetric title="AWS EC2 CPU Utilization" cloudService="aws" />
                <CloudMetric title="GCP Compute Engine Usage" cloudService="google" />
              </div>
              <div className="alerts">
                <Alert alertType="success" message="All systems are running smoothly." />
                <Alert alertType="warning" message="High CPU usage detected in AWS EC2 instance." />
              </div>
              <div className="login-buttons">
                <button onClick={handleGoogleLogin}>Login with Google</button>
                <button onClick={handleAWSLogin}>Login with AWS</button>
              </div>
            </>
          )}
          {activeSection === 'metrics' && <h2>Metrics Section</h2>}
          {activeSection === 'logs' && <h2>Logs Section</h2>}
          {activeSection === 'alerts' && <h2>Alerts Section</h2>}
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
