import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Dashboard from '../src/dashboard'; // Correct import path

const OAuthCallbackHandler = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const handleOAuthCallback = async () => {
      try {
        const response = await axios.get('http://localhost:5000/auth/status', { withCredentials: true });
        if (response.status === 200 && response.data.authenticated) {
          console.log('OAuth callback successful');
          navigate('/dashboard'); // Redirect to the dashboard or another protected route
        } else {
          console.error('OAuth callback failed');
          navigate('/'); // Redirect to the home page or login page
        }
      } catch (error) {
        console.error('Error handling OAuth callback:', error);
        navigate('/'); // Redirect to the home page or login page
      }
    };

    handleOAuthCallback();
  }, [navigate]);

  return null; // This component doesn't render anything
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/oauth2callback" element={<OAuthCallbackHandler />} />
        <Route path="/" element={<div><h1>Welcome to the App</h1></div>} />
      </Routes>
    </Router>
  );
};

export default App;
