import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { Chart as ChartJS } from 'chart.js/auto';

const CloudMetric = ({ title, cloudService }) => {
  const [metricData, setMetricData] = useState(null);

  useEffect(() => {
    // Fetch cloud metrics based on the service (AWS or GCP)
    const fetchMetrics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/metrics/${cloudService}`);
        setMetricData(response.data);
      } catch (error) {
        console.error("Error fetching metrics:", error);
      }
    };
    fetchMetrics();
  }, [cloudService]);

  if (!metricData) return <div>Loading...</div>;

  const data = {
    labels: metricData.timestamps,
    datasets: [
      {
        label: title,
        data: metricData.values,
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      },
    ],
  };

  return (
    <div className="metric-container">
      <h3>{title}</h3>
      <Line data={data} />
    </div>
  );
};

export default CloudMetric;
