import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import Sidebar from './Sidebar';
import CloudMetric from './CloudMetric';
import Alert from './Alert';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [googleLoginClicked, setGoogleLoginClicked] = useState(false);
  const [awsLoginClicked, setAwsLoginClicked] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      setIsAuthenticated(true);
    }
  }, [navigate]);

  useEffect(() => {
    if (googleLoginClicked || awsLoginClicked) {
      const handleOAuthCallback = async () => {
        try {
          const response = await axios.get('http://localhost:5000/auth/status', { withCredentials: true });
          if (response.status === 200 && response.data.authenticated) {
            console.log('OAuth callback successful');
            setIsAuthenticated(true);
          } else {
            console.error('OAuth callback failed');
            navigate('/login'); // Redirect to the login page
          }
        } catch (error) {
          console.error('Error handling OAuth callback:', error);
          navigate('/login'); // Redirect to the login page
        }
      };

      handleOAuthCallback();
    }
  }, [googleLoginClicked, awsLoginClicked, navigate]);

  const handleGoogleLogin = () => {
    setGoogleLoginClicked(true);
    window.location.href = 'http://localhost:5000/auth/google';
  };

  const handleAwsLogin = () => {
    setAwsLoginClicked(true);
    window.location.href = 'http://localhost:5000/auth/aws';
  };

  if (!isAuthenticated) {
    return null; // Render nothing while checking authentication
  }

  return (
    <div>
      <Header />
      <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} />
      <div>
        {activeSection === 'dashboard' && (
          <>
            <CloudMetric title="AWS CPU Utilization" cloudService="aws" />
            <CloudMetric title="Google CPU Utilization" cloudService="google" />
          </>
        )}
        {activeSection === 'alerts' && <Alert />}
        <button onClick={handleGoogleLogin}>Login with Google</button>
        <button onClick={handleAwsLogin}>Login with AWS</button>
      </div>
    </div>
  );
};

export default Dashboard;
