import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { getCloudMetrics, loginGoogle, loginAWS } from '/services/api';

const CloudMetric = ({ title, cloudService }) => {
  const [metricData, setMetricData] = useState(null);
  const [accessToken, setAccessToken] = useState(null);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        if (!accessToken) {
          if (cloudService === 'google') {
            await loginGoogle();
          } else if (cloudService === 'aws') {
            const awsToken = await loginAWS('your-username', 'your-password');
            setAccessToken(awsToken);
          }
        } else {
          const data = await getCloudMetrics(cloudService, accessToken);
          setMetricData(data);
        }
      } catch (error) {
        console.error("Error fetching metrics:", error);
      }
    };
    fetchMetrics();
  }, [cloudService, accessToken]);

  if (!metricData) return <div>Loading...</div>;

  const data = {
    labels: metricData.timestamps,
    datasets: [
      {
        label: title,
        data: metricData.values,
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      },
    ],
  };

  return (
    <div className="metric-container">
      <h3>{title}</h3>
      <Line data={data} />
    </div>
  );
};

export default CloudMetric;
