import React from 'react';

const Sidebar = ({ activeSection, setActiveSection }) => {
  return (
    <aside className="sidebar">
      <ul>
        <li className={activeSection === 'dashboard' ? 'active' : ''} onClick={() => setActiveSection('dashboard')}>Dashboard</li>
        <li className={activeSection === 'metrics' ? 'active' : ''} onClick={() => setActiveSection('metrics')}>Metrics</li>
        <li className={activeSection === 'logs' ? 'active' : ''} onClick={() => setActiveSection('logs')}>Logs</li>
        <li className={activeSection === 'alerts' ? 'active' : ''} onClick={() => setActiveSection('alerts')}>Alerts</li>
      </ul>
    </aside>
  );
};

export default Sidebar;
