import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import Sidebar from './Sidebar';
import CloudMetric from './CloudMetric';
import Alert from './Alert';
import { loginGoogle, loginAWS } from './services/api';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    const handleOAuthCallback = async () => {
      try {
        const response = await axios.get('http://localhost:5000/auth/status', { withCredentials: true });
        if (response.status === 200 && response.data.authenticated) {
          console.log('OAuth callback successful');
          // User is authenticated, stay on the dashboard
        } else {
          console.error('OAuth callback failed');
          navigate('/'); // Redirect to the home page or login page
        }
      } catch (error) {
        console.error('Error handling OAuth callback:', error);
        navigate('/'); // Redirect to the home page or login page
      }
    };

    handleOAuthCallback();
  }, [navigate]);

  const handleGoogleLogin = () => {
    loginGoogle();
  };

  const handleAWSLogin = async () => {
    try {
      const awsToken = await loginAWS('your-username', 'your-password');
      console.log('AWS Token:', awsToken);
    } catch (error) {
      console.error('Error logging in to AWS:', error);
    }
  };

  return (
    <div>
      <Header />
      <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} />
      <div>
        {activeSection === 'dashboard' && <CloudMetric />}
        {activeSection === 'alerts' && <Alert />}
      </div>
    </div>
  );
};

export default Dashboard;
