import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Dashboard from '../src/dashboard';

const App = () => {
  const history = useHistory();

  useEffect(() => {
    const handleOAuthCallback = async () => {
      try {
        const response = await axios.get('http://localhost:5000/auth/google/callback', { withCredentials: true });
        if (response.status === 200) {
          console.log('OAuth callback successful');
          history.push('/dashboard'); // Redirect to the dashboard or another protected route
        } else {
          console.error('OAuth callback failed');
          history.push('/'); // Redirect to the home page or login page
        }
      } catch (error) {
        console.error('Error handling OAuth callback:', error);
        history.push('/'); // Redirect to the home page or login page
      }
    };

    handleOAuthCallback();
  }, [history]);

  return (
    <div>
      <h1>Welcome to the App</h1>
    </div>
  );
};

export default App;
